import { Link } from "react-router-dom";
import useValidation from "../hooks/useValidation.js";

const Register = ({ name, onRegister }) => {
  const { values, errors, onChange } = useValidation();

  const handleSubmit = (e) => {
    e.preventDefault();
    onRegister(values);
  };

  return (
    <div className="register">
      <h2 className="register__title">Регистрация</h2>
      <form
        className="register__form"
        name={`${name}`}
        onSubmit={handleSubmit}
        noValidate
      >
        <input
          type="email"
          value={values.email || ""}
          onChange={onChange}
          className="register__input"
          name="email"
          id="email"
          placeholder="email@mail.com"
          autoComplete="off"
          required
        />
        <span className="error register__error">{errors.email}</span>
        <input
          type="password"
          value={values.password || ""}
          onChange={onChange}
          className="register__input"
          name="password"
          id="password"
          placeholder="••••••••••"
          autoComplete="off"
          minLength="8"
          required
        />
        <span className="error register__error">{errors.password}</span>
        <button className="register__submit-button" type="submit">
          Зарегистрироваться
        </button>
      </form>
      <div className="register__signin">
        <p className="register__signin-text">Уже зарегистрированы?</p>
        <Link to="/sign-in" className="register__signin-link">
          Войти
        </Link>
      </div>
    </div>
  );
};

export default Register;
